var mysql=require('mysql');
var pool=mysql.createPool({
	host:'localhost',
	user:'root',
	password:'',
	database:'xremind'
});

exports.query=function(sql,cb){
	pool.getConnection(function(err,conn){
		if(err){
			cb(err,null,null);
		}else{
			conn.query(sql,function(qerr,vals,fields){
				conn.release();//释放连接
				cb(qerr,vals,fields);
			});
		}
	});
}